  `email` varchar(100) DEFAULT '' COMMENT '邮箱',
  `mobile` char(15) DEFAULT '' COMMENT '手机号码',
  `mchild` tinyint(1) NOT NULL COMMENT '会员类别：1平台会员，2品牌商，3代理商，4商户',
