  `login_counts` int(11) unsigned DEFAULT '0' COMMENT '登陆次数',
  `update_time` int(10) DEFAULT NULL COMMENT '更新时间',
  `del` enum('false','true') DEFAULT 'false' COMMENT '用户假删除true：已删除，false:未删除',
  `status` enum('false','true') DEFAULT 'true' COMMENT '会员状态 false:用户禁用，true：正常用户',
  PRIMARY KEY (`mid`),
  UNIQUE KEY `mname` (`mname`)
) ENGINE=InnoDB AUTO_INCREMENT=513 DEFAULT CHARSET=utf8;

